import * as Yup from 'yup';

export const esquemaValidacao = Yup.object().shape({
    nome: Yup.string().max(30, 'O nome da clinica deve ter no máximo 30 caracteres!').required('O nome da clínica é obrigatório!'),
    npj: Yup.string()
      .required('CNPJ é obrigatório')
      .matches(/^\d{2}\.\d{3}\.\d{3}\/\d{4}\-\d{2}$/, 'CNPJ deve seguir o formato 00.000.000/0000-00')
      .test('valida-cnpj', 'CNPJ inválido', valor => {
        // Removendo a máscara para contar os dígitos
        const cnpjNumeros = valor.replace(/[^\d]/g, '');
        return cnpjNumeros.length === 14;
      }),
})

export {}
